import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api";
import Button from "react-bootstrap/Button";

export default function DocumentViewer() {
  const { id } = useParams();
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    API.get(`/docs/${id}`).then((res) => setText(res.data.text));
  }, [id]);

  const summarise = async () => {
    setLoading(true);
    const res = await API.post("/docs/summarise", { text });
    setSummary(res.data.summary);
    setLoading(false);
  };

  return (
    <div className="container mt-3">
      <h3>Document Viewer</h3>

      <pre style={{ whiteSpace: "pre-wrap", background: "#f8f9fa", padding: "10px" }}>
        {text}
      </pre>

      <Button variant="primary" onClick={summarise} disabled={loading}>
        {loading ? "Summarising..." : "Summarise with AI"}
      </Button>

      {summary && (
        <div className="alert alert-info mt-3">
          <h5>AI Summary</h5>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}
