import React, {useEffect,useState} from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';
export default function CoursePage(){
  const {id} = useParams();
  const [course,setCourse] = useState(null);
  const [sections,setSections] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  useEffect(()=>{ API.get('/courses/'+id).then(r=>{ setCourse(r.data.course); }); },[id]);
  const handleEnroll = async ()=> {
    await API.post(`/courses/${id}/enroll`);
    const secs = await API.get(`/courses/${id}/sections`);
    setSections(secs.data);
  };
  const loadVideos = async (sectionId) => {
    const v = await API.get(`/courses/section/${sectionId}/videos`);
    setSelectedVideo(v.data[0]?._id || null);
    // show list in console
    console.log(v.data);
  };
  return (
    <div className="container mt-4">
      {!course ? <p>Loading...</p> : (
        <>
          <h2>{course.title}</h2>
          <p>{course.description}</p>
          <button className="btn btn-success mb-3" onClick={handleEnroll}>Enroll (Test)</button>
          <div className="row">
            <div className="col-md-4">
              <h5>Sections</h5>
              <ul className="list-group">
                {sections.map(s=>(
                  <li key={s._id} className="list-group-item">
                    <button className="btn btn-link" onClick={()=>loadVideos(s._id)}>{s.title}</button>
                  </li>
                ))}
              </ul>
            </div>
            <div className="col-md-8">
              {selectedVideo ? <iframe className="w-100" style={{height:400}} src={'/api/courses/video/'+selectedVideo} title="video" /> : <p>Select a section to load videos</p>}
            </div>
          </div>
        </>
      )}
    </div>
  );
}
