import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";
import DocumentViewer from "./DocumentViewer";

export default function CoursePage() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [sections, setSections] = useState([]);
  const [videos, setVideos] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [selectedDoc, setSelectedDoc] = useState(null);

  const nav = useNavigate();

  // useEffect(() => {
  //   API.get(`/courses/${id}`).then((res) => setCourse(res.data.course));
  // }, [id]);
  useEffect(() => {
    API.get(`/courses/${id}`).then((res) => setCourse(res.data.course));

    // LOAD DOCUMENTS
    // API.get(`/docs/course/${id}`).then((res) => {
    //   setDocuments(res.data);
    // });
  }, [id]);

  const handleEnroll = async () => {
    await API.post(`/courses/${id}/enroll`);
    const sec = await API.get(`/courses/${id}/sections`);
    setSections(sec.data);
  };

  // const docs = await API.get(`/docs/course/${id}`);
  // setDocuments(docs.data);


  const loadVideos = async (sectionId) => {
    const res = await API.get(`/courses/section/${sectionId}/videos`);
    setVideos(res.data);
  };

  return (
    <div className="container mt-4">

      {!course ? (
        <p>Loading...</p>
      ) : (
        <>
          <h2>{course.title}</h2>
          <p>{course.description}</p>

          <button className="btn btn-success mb-3" onClick={handleEnroll}>
            Enroll
          </button>

          <div className="row">

            {/* LEFT SIDE */}
            <div className="col-md-4">

              <h5>Sections</h5>
              <ul className="list-group">
                {sections.map((s) => (
                  <li key={s._id} className="list-group-item">
                    <button className="btn btn-link p-0" onClick={() => loadVideos(s._id)}>
                      {s.title}
                    </button>
                  </li>
                ))}
              </ul>

              {/* DOCUMENTS */}
              <h5 className="mt-4">Documents</h5>
              <ul className="list-group">
                {documents.map((d) => (
                  <li
                    key={d._id}
                    className="list-group-item"
                    style={{ cursor: "pointer" }}
                    onClick={() => setSelectedDoc(d)}
                  >
                    ðŸ“„ {d.title}
                  </li>
                ))}
              </ul>
              {/* <h5 className="mt-4">Upload Document</h5>

              <input
                type="file"
                className="form-control mb-2"
                onChange={async (e) => {
                  const form = new FormData();
                  form.append("document", e.target.files[0]);

                  const res = await API.post(`/docs/upload/${id}`, form, {
                    headers: { "Content-Type": "multipart/form-data" }
                  });

                  // Refresh document list
                  const docs = await API.get(`/docs/course/${id}`);
                  setDocuments(docs.data);
                }}
              /> */}


              <h6 className="mt-3">Videos</h6>
              <ul className="list-group">
                {videos.map((v) => (
                  <li
                    key={v._id}
                    className="list-group-item"
                    style={{ cursor: "pointer" }}
                    onClick={() => nav(`/video/${v._id}`)}
                  >
                    ðŸŽ¬ {v.title}
                  </li>
                ))}
              </ul>

            </div>

            {/* RIGHT SIDE â€” Show Document Viewer */}
            <div className="col-md-8">
              {selectedDoc ? (
                <DocumentViewer doc={selectedDoc} />
              ) : (
                <p>Select a video or document to open.</p>
              )}
            </div>

          </div>
        </>
      )}
    </div>
  );
}
