import React, {useState} from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';
export default function Login(){
  const [email,setEmail]=useState(''); const [password,setPassword]=useState('');
  const nav = useNavigate();
  const submit = async(e)=>{ e.preventDefault(); const r = await API.post('/auth/login',{email,password}); localStorage.setItem('token', r.data.token); nav('/'); };
  return (<div className="container mt-5" style={{maxWidth:420}}><h3>Login</h3><form onSubmit={submit}><input className="form-control mb-2" value={email} onChange={e=>setEmail(e.target.value)} placeholder="email"/><input type="password" className="form-control mb-2" value={password} onChange={e=>setPassword(e.target.value)} placeholder="password"/><button className="btn btn-primary w-100">Login</button></form></div>);
}
