import React, { useState } from "react";
import API from "../api";

export default function DocumentViewer({ text }) {
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  const summarise = async () => {
    setLoading(true);
    const res = await API.post("/docs/summarise", { text });
    setSummary(res.data.summary);
    setLoading(false);
  };

  return (
    <div>
      <h4>Document</h4>

      <pre
        style={{
          whiteSpace: "pre-wrap",
          background: "#f8f9fa",
          padding: "10px",
          borderRadius: "4px",
        }}
      >
        {text}
      </pre>

      <button
        className="btn btn-primary"
        onClick={summarise}
        disabled={loading}
      >
        {loading ? "Summarising..." : "Summarise with AI"}
      </button>

      {summary && (
        <div className="alert alert-info mt-3">
          <h5>AI Summary</h5>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}
