import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api";

export default function DocumentViewer() {
  const { id } = useParams();
  const [doc, setDoc] = useState(null);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    API.get(`/docs/${id}`).then((res) => setDoc(res.data));
  }, [id]);

  const handleSummarise = async () => {
    setLoading(true);
    const res = await API.post("/docs/summarise", { text: doc.text });
    setSummary(res.data.summary);
    setLoading(false);
  };

  if (!doc) return <p>Loading...</p>;

  return (
    <div className="container mt-4">
      <h3>{doc.name}</h3>

      <pre style={{ whiteSpace: "pre-wrap", background: "#f8f9fa", padding: 10 }}>
        {doc.text}
      </pre>

      <button className="btn btn-primary" onClick={handleSummarise}>
        {loading ? "Summarising..." : "Summarise with AI"}
      </button>

      {summary && (
        <div className="alert alert-info mt-3">
          <h5>AI Summary</h5>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
}
