import React from "react";
import { useNavigate } from "react-router-dom";

export default function LandingPage() {
  const nav = useNavigate();

  return (
    <div className="container mt-4">
      {/* Page Title */}
      <h1 className="text-center mb-4 fw-bold">Welcome to Imarticus LMS</h1>

      {/* Cards Section */}
      <div className="row justify-content-center">

        {/* Courses Card */}
        <div className="col-md-4 mb-4">
          <div
            className="card shadow rounded-4 p-4 text-center"
            style={{ cursor: "pointer", borderLeft: "6px solid #198754" }}
            onClick={() => nav("/")}
          >
            <h3 className="fw-bold mb-2">Courses</h3>
            <p>View all available courses and start learning.</p>
          </div>
        </div>

        {/* Assignments Card */}
        <div className="col-md-4 mb-4">
          <div
            className="card shadow rounded-4 p-4 text-center"
            style={{ cursor: "pointer", borderLeft: "6px solid #0d6efd" }}
          >
            <h3 className="fw-bold mb-2">Assignments</h3>
            <p>Check and complete your course assignments.</p>
          </div>
        </div>

        {/* Progress Card */}
        <div className="col-md-4 mb-4">
          <div
            className="card shadow rounded-4 p-4 text-center"
            style={{ cursor: "pointer", borderLeft: "6px solid #ffc107" }}
          >
            <h3 className="fw-bold mb-2">Progress</h3>
            <p>Track your learning and achievement milestones.</p>
          </div>
        </div>

      </div>
    </div>
  );
}
