// CoursePage.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api";

export default function CoursePage() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [sections, setSections] = useState([]);
  const [videos, setVideos] = useState([]);
  const nav = useNavigate();

  useEffect(() => {
    API.get(`/courses/${id}`).then((res) => setCourse(res.data.course));
  }, [id]);

  const handleEnroll = async () => {
    await API.post(`/courses/${id}/enroll`);
    const sec = await API.get(`/courses/${id}/sections`);
    setSections(sec.data);
  };

  const loadVideos = async (sectionId) => {
    const res = await API.get(`/courses/section/${sectionId}/videos`);
    setVideos(res.data);
  };

  return (
    <div className="container mt-4">

      {!course ? (
        <p>Loading...</p>
      ) : (
        <>
          <h2>{course.title}</h2>
          <p>{course.description}</p>

          <button className="btn btn-success mb-3" onClick={handleEnroll}>
            Enroll
          </button>

          <div className="row">

            {/* LEFT â€” Sections & Videos */}
            <div className="col-md-4">

              <h5>Sections</h5>
              <ul className="list-group">
                {sections.map((s) => (
                  <li key={s._id} className="list-group-item">
                    <button className="btn btn-link p-0" onClick={() => loadVideos(s._id)}>
                      {s.title}
                    </button>
                  </li>
                ))}
              </ul>

              <h6 className="mt-3">Videos</h6>
              <ul className="list-group">
                {videos.map((v) => (
                  <li
                    key={v._id}
                    className="list-group-item"
                    style={{ cursor: "pointer" }}
                    onClick={() => nav(`/video/${v._id}`)}
                  >
                    ðŸŽ¬ {v.title}
                  </li>
                ))}
              </ul>

            </div>

            {/* RIGHT AREA EMPTY NOW (video plays fullscreen) */}
            <div className="col-md-8">
              <p>Select a video to watch.</p>
            </div>

          </div>
        </>
      )}
    </div>
  );
}

