import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';

export default function CoursePage() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [sections, setSections] = useState([]);
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    API.get('/courses/' + id).then((r) => {
      setCourse(r.data.course);
    });
  }, [id]);

  const handleEnroll = async () => {
    await API.post(`/courses/${id}/enroll`);
    const secs = await API.get(`/courses/${id}/sections`);
    setSections(secs.data);
  };

  const loadVideos = async (sectionId) => {
    const res = await API.get(`/courses/section/${sectionId}/videos`);
    setVideos(res.data);

    // auto-select first video
    if (res.data.length > 0) {
      setSelectedVideo(res.data[0]);
    }
  };

  // Convert normal YouTube URL â†’ embed URL
  const getEmbedUrl = (url) => {
    if (!url) return "";
    const videoId = url.split("v=")[1];
    return `https://www.youtube.com/embed/${videoId}`;
  };

  return (
    <div className="container mt-4">

      {!course ? (
        <p>Loading...</p>
      ) : (
        <>
          <h2>{course.title}</h2>
          <p>{course.description}</p>

          <button className="btn btn-success mb-3" onClick={handleEnroll}>
            Enroll
          </button>

          <div className="row">

            {/* Left Column â€“ Sections */}
            <div className="col-md-4">
              <h5>Sections</h5>
              <ul className="list-group">
                {sections.map((s) => (
                  <li key={s._id} className="list-group-item">
                    <button
                      className="btn btn-link p-0"
                      onClick={() => loadVideos(s._id)}
                    >
                      {s.title}
                    </button>
                  </li>
                ))}
              </ul>

              {/* Videos List */}
              <h6 className="mt-3">Videos</h6>
              <ul className="list-group">
                {videos.map((v) => (
                  <li
                    key={v._id}
                    className="list-group-item"
                    onClick={() => setSelectedVideo(v)}
                    style={{ cursor: "pointer" }}
                  >
                    ðŸ“º {v.title}
                  </li>
                ))}
              </ul>
            </div>

            {/* Right Column â€“ Video Player */}
            <div className="col-md-8">
              {selectedVideo ? (
                <iframe
                  className="w-100"
                  style={{ height: 400 }}
                  src={getEmbedUrl(selectedVideo.url)}
                  title="YouTube video player"
                  allowFullScreen
                />
              ) : (
                <p>Select a section and video</p>
              )}
            </div>

          </div>
        </>
      )}

    </div>
  );
}
